#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, k;
  cin >> n >> k;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long count = 0;
  for (long long i = 0; i < n; ++i) {
    long long sum = 0;
    for (long long j = i; j < n; ++j) {
      sum += a[j];
      if (sum * 1.0 / (j - i + 1) >= k) {
        count++;
      }
    }
  }
  cout << count << endl;
  return 0;
}
