#include <stdio.h>
#include <stdlib.h>
#include <string.h>

long long rev(long long n) {
    long long rev_n = 0;
    while (n > 0) {
        rev_n = rev_n * 10 + n % 10;
        n /= 10;
    }
    return rev_n;
}

int main() {
    long long d;
    scanf("%lld", &d);
    long long count = 0;
    for (long long n = 1; n <= 10000000; ++n) {
        if (rev(n) == n + d) {
            count++;
        }
    }
    printf("%lld\n", count);
    return 0;
}
