#include <iostream>
#include <algorithm>

long long rev(long long n) {
    long long res = 0;
    while (n > 0) {
        res = res * 10 + n % 10;
        n /= 10;
    }
    return res;
}

int main() {
    long long D;
    std::cin >> D;
    long long count = 0;
    for (long long i = 1; i <= 1000000; ++i) {
        if (rev(i) == i + D) {
            count++;
        }
    }
    std::cout << count << std::endl;
    return 0;
}
