import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long a = sc.nextLong();
        long b = sc.nextLong();
        if (a == b) {
            System.out.println(1);
        } else {
            System.out.println((b - a) * n + 1);
        }
    }
}
