#include <stdio.h>
#include <string.h>

int main() {
    char s[100005];
    scanf("%s", s);
    int n = strlen(s);
    long long ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            int count = 0;
            int cur = i;
            if (i == j) continue;
            while (cur != j) {
                if (cur < j) {
                    if (s[cur] == 'U') {
                        cur++;
                    } else {
                        int k = cur;
                        while (k < j && s[k] == 'D') k++;
                        if (k == j) {
                            cur = j;
                        } else {
                            cur = k;
                        }

                    }
                } else {
                    if (s[cur] == 'D') {
                        cur--;
                    } else {
                        int k = cur;
                        while (k > j && s[k] == 'U') k--;
                        if (k == j) {
                            cur = j;
                        } else {
                            cur = k;
                        }
                    }
                }
                count++;
            }
            ans += count;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
