import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int n = s.length();
        long ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                int count = 0;
                int cur = i;
                while (cur != j) {
                    if (cur < j) {
                        if (s.charAt(cur) == 'U') {
                            cur++;
                        } else {
                            int next = cur;
                            while (next < j && s.charAt(next) == 'D') {
                                next++;
                            }
                            if(next == j) cur = j;
                            else {
                                cur = next;
                            }
                        }
                    } else {
                        if (s.charAt(cur) == 'D') {
                            cur--;
                        } else {
                            int next = cur;
                            while (next > j && s.charAt(next) == 'U') {
                                next--;
                            }
                            if(next == j) cur = j;
                            else {
                                cur = next;
                            }
                        }
                    }
                    count++;
                }
                ans += count;
            }
        }
        System.out.println(ans);
    }
}
