s = input()
n = len(s)
ans = 0
for i in range(n):
    for j in range(n):
        if i == j:
            continue
        count = 0
        cur = i
        if i < j:
            while cur < j:
                if s[cur] == 'U':
                    cur += 1
                else:
                    cur_up = cur
                    while cur_up < j and s[cur_up] == 'D':
                        cur_up +=1
                    if cur_up == j:
                        cur = cur_up
                    else:
                        cur = cur_up

                count += 1

        else:
            while cur > j:
                if s[cur] == 'D':
                    cur -= 1
                else:
                    cur_down = cur
                    while cur_down > j and s[cur_down] == 'U':
                        cur_down -= 1
                    if cur_down == j:
                        cur = cur_down
                    else:
                        cur = cur_down
                count += 1
        ans += count
print(ans)

