#include <stdio.h>

#define MAXN 2005
#define MAXQ 200005

int n, m, q;
int s[MAXN][MAXN];
int visited[MAXN][MAXN];

int dx[] = {0, 0, 1, -1};
int dy[] = {1, -1, 0, 0};

int dfs(int x, int y, int x1, int y1, int x2, int y2) {
    if (x < x1 || x > x2 || y < y1 || y > y2 || s[x][y] == 0 || visited[x][y]) return 0;
    visited[x][y] = 1;
    for (int i = 0; i < 4; i++) {
        dfs(x + dx[i], y + dy[i], x1, y1, x2, y2);
    }
    return 1;
}

int main() {
    scanf("%d %d %d", &n, &m, &q);
    for (int i = 1; i <= n; i++) {
        char row[MAXN];
        scanf("%s", row);
        for (int j = 1; j <= m; j++) {
            s[i][j] = row[j - 1] - '0';
        }
    }
    for (int i = 0; i < q; i++) {
        int x1, y1, x2, y2;
        scanf("%d %d %d %d", &x1, &y1, &x2, &y2);
        int count = 0;
        for (int j = x1; j <= x2; j++) {
            for (int k = y1; k <= y2; k++) {
                visited[j][k] = 0;
            }
        }
        for (int j = x1; j <= x2; j++) {
            for (int k = y1; k <= y2; k++) {
                if (s[j][k] == 1 && !visited[j][k]) {
                    count += dfs(j, k, x1, y1, x2, y2);
                }
            }
        }
        printf("%d\n", count);
    }
    return 0;
}
