#include <iostream>
#include <vector>

using namespace std;

int n, m, q;
vector<vector<int>> s;

int dx[] = {0, 0, 1, -1};
int dy[] = {1, -1, 0, 0};

int dfs(int x, int y, int x1, int y1, int x2, int y2, vector<vector<bool>>& visited) {
    if (x < x1 || x > x2 || y < y1 || y > y2 || s[x][y] == 0 || visited[x][y]) return 0;
    visited[x][y] = true;
    int count = 1;
    for (int i = 0; i < 4; ++i) {
        count += dfs(x + dx[i], y + dy[i], x1, y1, x2, y2, visited);
    }
    return count;
}

int main() {
    cin >> n >> m >> q;
    s.resize(n + 1, vector<int>(m + 1));
    for (int i = 1; i <= n; ++i) {
        string row;
        cin >> row;
        for (int j = 1; j <= m; ++j) {
            s[i][j] = row[j - 1] - '0';
        }
    }

    for (int i = 0; i < q; ++i) {
        int x1, y1, x2, y2;
        cin >> x1 >> y1 >> x2 >> y2;
        int count = 0;
        vector<vector<bool>> visited(n + 1, vector<bool>(m + 1, false));
        for (int j = x1; j <= x2; ++j) {
            for (int k = y1; k <= y2; ++k) {
                if (s[j][k] == 1 && !visited[j][k]) {
                    dfs(j, k, x1, y1, x2, y2, visited);
                    count++;
                }
            }
        }
        cout << count << endl;
    }
    return 0;
}
