import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int q = sc.nextInt();
        int[][] s = new int[n][m];
        for (int i = 0; i < n; i++) {
            String str = sc.next();
            for (int j = 0; j < m; j++) {
                s[i][j] = str.charAt(j) - '0';
            }
        }
        for (int i = 0; i < q; i++) {
            int x1 = sc.nextInt() - 1;
            int y1 = sc.nextInt() - 1;
            int x2 = sc.nextInt() - 1;
            int y2 = sc.nextInt() - 1;
            int[][] t = new int[x2 - x1 + 1][y2 - y1 + 1];
            for (int j = 0; j <= x2 - x1; j++) {
                for (int k = 0; k <= y2 - y1; k++) {
                    t[j][k] = s[x1 + j][y1 + k];
                }
            }
            int count = 0;
            boolean[][] visited = new boolean[x2 - x1 + 1][y2 - y1 + 1];
            for (int j = 0; j <= x2 - x1; j++) {
                for (int k = 0; k <= y2 - y1; k++) {
                    if (t[j][k] == 1 && !visited[j][k]) {
                        count++;
                        dfs(t, visited, j, k);
                    }
                }
            }
            System.out.println(count);
        }
    }
    public static void dfs(int[][] t, boolean[][] visited, int x, int y) {
        if (x < 0 || x >= t.length || y < 0 || y >= t[0].length || t[x][y] == 0 || visited[x][y]) {
            return;
        }
        visited[x][y] = true;
        dfs(t, visited, x + 1, y);
        dfs(t, visited, x - 1, y);
        dfs(t, visited, x, y + 1);
        dfs(t, visited, x, y - 1);
    }
}
