def solve():
    n = int(input())
    t = list(map(int, input().split()))
    v = list(map(int, input().split()))

    total_time = sum(t)
    ans = 0.0

    dp = {}
    dp[0, 0] = 0

    for i in range(n):
        new_dp = {}
        for (cur_time, cur_vel), cur_dist in dp.items():
            for accel in [-1, 0, 1]:
                next_vel = cur_vel + accel * t[i]
                next_time = cur_time + t[i]
                next_dist = cur_dist + cur_vel * t[i] + 0.5 * accel * t[i] ** 2

                if abs(next_vel) <= v[i]:
                    if (next_time, next_vel) not in new_dp or new_dp[(next_time, next_vel)] < next_dist:
                        new_dp[(next_time, next_vel)] = next_dist

        dp = new_dp

    max_dist = 0
    for (cur_time, cur_vel), cur_dist in dp.items():
        if cur_time == total_time:

            deceleration_time = cur_vel
            deceleration_dist = 0.5 * cur_vel * deceleration_time
            max_dist = max(max_dist, cur_dist + deceleration_dist)

    print(max_dist)

