#include <iostream>
#include <set>

using namespace std;

int main() {
  long long a, b;
  cin >> a >> b;

  set<long long> s;
  for (long long i = a; i <= b; ++i) {
    s.insert(i);
  }

  set<long long> or_values;
  or_values.insert(0);

  for (long long i = 0; i < (1LL << s.size()); ++i) {
    long long current_or = 0;
    long long k = 0;
    for (long long x : s) {
      if ((i >> k) & 1) {
        current_or |= x;
      }
      k++;
    }
    if (current_or != 0)
      or_values.insert(current_or);
  }

  cout << or_values.size() << endl;
  return 0;
}
