#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<pair<long long, long long>> takahashi(n);
  for (int i = 0; i < n; ++i) {
    cin >> takahashi[i].first >> takahashi[i].second;
  }

  long long count = 0;
  long long mod = 1e9 + 7;

  for (int i = 0; i < (1 << n); ++i) {
    vector<pair<long long, long long>> current_takahashi = takahashi;
    vector<pair<long long, long long>> aoki;

    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        aoki.push_back(current_takahashi[j]);
      }
    }

    bool all_aoki = false;
    while (true) {
      bool changed = false;
      vector<pair<long long, long long>> next_takahashi;
      for (int j = 0; j < current_takahashi.size(); ++j) {
        bool collision = false;
        for (int k = 0; k < aoki.size(); ++k) {
          if (current_takahashi[j].first == aoki[k].first && current_takahashi[j].second == aoki[k].second) continue;
          if (current_takahashi[j].second == aoki[k].second && (current_takahashi[j].first < aoki[k].first) )
          {
            if( (aoki[k].first - current_takahashi[j].first) % (current_takahashi[j].second - aoki[k].second) ==0 && (aoki[k].first - current_takahashi[j].first) / (current_takahashi[j].second - aoki[k].second) >=0)
            {
              collision = true;
              break;
            }
          }
          else if (current_takahashi[j].second == aoki[k].second && (current_takahashi[j].first > aoki[k].first) )
          {
             if( (current_takahashi[j].first - aoki[k].first) % (aoki[k].second - current_takahashi[j].second) ==0 && (current_takahashi[j].first - aoki[k].first) / (aoki[k].second - current_takahashi[j].second) >=0 )
             {
                collision = true;
                break;
             }
          }
        }
        if (collision) {
          changed = true;
        } else {
          next_takahashi.push_back(current_takahashi[j]);
        }
      }
      current_takahashi = next_takahashi;
      if (!changed) break;

    }
    if (current_takahashi.empty()) {
      count++;
    }
  }

  cout << count << endl;
  return 0;
}
