import java.util.*;
import java.lang.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[][] arr = new long[n][2];
        for (int i = 0; i < n; i++) {
            arr[i][0] = sc.nextLong();
            arr[i][1] = sc.nextLong();
        }
        long count = 0;
        long mod = 1000000007;
        for (int i = 0; i < (1 << n); i++) {
            List<Long> aoki = new ArrayList<>();
            List<Long> taka = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    aoki.add(arr[j][0]);
                    aoki.add(arr[j][1]);
                } else {
                    taka.add(arr[j][0]);
                    taka.add(arr[j][1]);
                }
            }
            boolean flag = true;
            if (taka.size() == 0) {
                count++;
                continue;
            }
            for (int j = 0; j < taka.size(); j += 2) {
                boolean found = false;
                for (int k = 0; k < aoki.size(); k += 2) {
                    if (taka.get(j + 1) == aoki.get(k + 1) && taka.get(j) < aoki.get(k)) {
                        found = true;
                        break;
                    }
                }
                if (!found) {
                    flag = false;
                    break;
                }
            }
            if (flag) {
                count++;
            }
        }
        System.out.println(count);
    }
}
