def solve():
    n = int(input())
    xv = []
    for _ in range(n):
        xv.append(list(map(int, input().split())))

    count = 0
    for i in range(1 << n):
        aoki = []
        takahashi = []
        for j in range(n):
            if (i >> j) & 1:
                aoki.append(xv[j])
            else:
                takahashi.append(xv[j])


        all_aoki = True
        for k in range(len(takahashi)):
          found = False
          for l in range(len(aoki)):
            if takahashi[k][1] == aoki[l][1] and takahashi[k][0] < aoki[l][0]:
              found = True
              break
          if not found:
            all_aoki = False
            break

        if all_aoki:
            count += 1

    print(count)

