#include <stdio.h>

long long gcd_steps(long long a, long long b) {
    if (b == 0) return 0;
    if (a == 0) return 0;
    if (a > b) {
        long long temp = a;
        a = b;
        b = temp;
    }
    return gcd_steps(b % a, a) + 1;
}

int main() {
    int q;
    scanf("%d", &q);
    while (q--) {
        long long x, y;
        scanf("%lld %lld", &x, &y);
        long long max_steps = 0;
        long long count = 0;
        for (long long i = 1; i <= x; i++) {
            for (long long j = 1; j <= y; j++) {
                long long steps = gcd_steps(i, j);
                if (steps > max_steps) {
                    max_steps = steps;
                    count = 2;
                } else if (steps == max_steps && steps > 0) {
                    count +=2;
                }
            }
        }

        long long mod = 1000000007;
        printf("%lld %lld\n", max_steps, count % mod);

    }
    return 0;
}
