#include <iostream>
#include <algorithm>

using namespace std;

int gcd_steps(long long a, long long b) {
  if (b == 0) return 0;
  if (a == 0) return 0;
  if (a > b) swap(a, b);
  return gcd_steps(b % a, a) + 1;
}

int main() {
  int q;
  cin >> q;

  long long mod = 1e9 + 7;

  while (q--) {
    long long x, y;
    cin >> x >> y;

    int max_steps = 0;
    long long count = 0;

    for (long long i = 1; i <= x; ++i) {
      for (long long j = 1; j <= y; ++j) {
        int steps = gcd_steps(i, j);
        if (steps > max_steps) {
          max_steps = steps;
          count = 1;
        } else if (steps == max_steps) {
          count++;
        }
      }
    }
    cout << max_steps << " " << count % mod << endl;
  }
  return 0;
}
