import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long z = sc.nextLong();
        long w = sc.nextLong();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long[][] dp = new long[n + 1][2];
        dp[n][0] = Math.abs(z - w);
        dp[n][1] = Math.abs(z - w);
        for (int i = n - 1; i >= 0; i--) {
            long max = 0;
            for (int j = i; j < n; j++) {
                long min = Long.MAX_VALUE;
                for (int k = j + 1; k < n; k++) {
                    min = Math.min(min, dp[k][1]);
                }
                max = Math.max(max, Math.abs(a[j] - w) - min);
            }
            dp[i][0] = max;
            long min = Long.MAX_VALUE;
            for (int j = i; j < n; j++) {
                long max1 = 0;
                for (int k = j + 1; k < n; k++) {
                    max1 = Math.max(max1, dp[k][0]);
                }
                min = Math.min(min, Math.abs(a[j] - z) + max1);
            }
            dp[i][1] = min;
        }
        System.out.println(dp[0][0]);
    }
}
