import java.util.*;

public class Main {
    static final long MOD = 1000000007;

    static int steps(long a, long b) {
        if (b == 0) return 0;
        return steps(b % a, a) + 1;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int q = sc.nextInt();
        while (q-- > 0) {
            long x = sc.nextLong();
            long y = sc.nextLong();
            int maxSteps = 0;
            long count = 0;
            for (long i = 1; i <= x; i++) {
                for (long j = 1; j <= y; j++) {
                    int s = steps(i, j);
                    if (s > maxSteps) {
                        maxSteps = s;
                        count = 1;
                    } else if (s == maxSteps) {
                        count++;
                    }
                }
            }
            System.out.println(maxSteps + " " + count);
        }
    }
}
