import sys

def gcd_steps(a, b):
    steps = 0
    while b:
        a, b = b, a % b
        steps += 1
    return steps

def solve():
    q = int(sys.stdin.readline())
    MOD = 10**9 + 7

    for _ in range(q):
        x, y = map(int, sys.stdin.readline().split())
        max_steps = 0
        count = 0

        for i in range(1, int(x**0.5) + 1):
            for j in range(i, int(y**0.5) +1):
                steps = gcd_steps(i,j)
                if steps > max_steps:
                    max_steps = steps
                    count = 2
                elif steps == max_steps and steps > 0:
                    count +=2




        print(max_steps, count % MOD)

solve()
