import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int H = scanner.nextInt();
        int W = scanner.nextInt();
        scanner.nextLine(); 

        String[] image = new String[H];
        for (int i = 0; i < H; i++) {
            image[i] = scanner.nextLine();
        }

        StringBuilder[] result = new StringBuilder[H + 2];
        for (int i = 0; i < H + 2; i++) {
            result[i] = new StringBuilder();
        }

        for (int i = 0; i < W + 2; i++) {
            result[0].append("#");
            result[H + 1].append("#");
        }

        for (int i = 1; i <= H; i++) {
            result[i].append("#");
            result[i].append(image[i - 1]);
            result[i].append("#");
        }

        for (int i = 0; i < H + 2; i++) {
            System.out.println(result[i]);
        }
        scanner.close();
    }
}
