#include <stdio.h>
#include <stdlib.h>

int main() {
    long long n;
    scanf("%lld", &n);
    long long a[3 * n];
    for (long long i = 0; i < 3 * n; i++) {
        scanf("%lld", &a[i]);
    }
    long long max_score = -1e18; 
    for (long long i = 0; i < (1LL << (3 * n)); i++) {
        if (__builtin_popcountll(i) != n) continue;
        long long b[2 * n];
        long long k = 0;
        for (long long j = 0; j < 3 * n; j++) {
            if (!((i >> j) & 1)) {
                b[k++] = a[j];
            }
        }
        long long sum1 = 0;
        for (long long j = 0; j < n; j++) {
            sum1 += b[j];
        }
        long long sum2 = 0;
        for (long long j = n; j < 2 * n; j++) {
            sum2 += b[j];
        }
        max_score = max_score > sum1 - sum2 ? max_score : sum1 - sum2;
    }
    printf("%lld\n", max_score);
    return 0;
}
