#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n;
  cin >> n;

  vector<long long> a(3 * n);
  for (int i = 0; i < 3 * n; ++i) {
    cin >> a[i];
  }

  long long max_score = -1e18; 

  for (int i = 0; i < (1 << (3 * n)); ++i) {
    if (__builtin_popcount(i) != 2 * n) continue;

    vector<long long> b;
    for (int j = 0; j < 3 * n; ++j) {
      if ((i >> j) & 1) {
        b.push_back(a[j]);
      }
    }

    long long current_score = 0;
    for (int j = 0; j < n; ++j) {
      current_score += b[j];
    }
    for (int j = n; j < 2 * n; ++j) {
      current_score -= b[j];
    }
    max_score = max(max_score, current_score);
  }
  cout << max_score << endl;
  return 0;
}
