import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[3 * n];
        for (int i = 0; i < 3 * n; i++) {
            a[i] = sc.nextLong();
        }
        long maxScore = Long.MIN_VALUE;
        for (int i = 0; i < (1 << (3 * n)); i++) {
            if (Integer.bitCount(i) != n) continue;
            List<Long> b = new ArrayList<>();
            for (int j = 0; j < 3 * n; j++) {
                if ((i >> j & 1) == 0) {
                    b.add(a[j]);
                }
            }
            if (b.size() != 2 * n) continue;
            long sumFirstHalf = 0;
            long sumSecondHalf = 0;
            for (int j = 0; j < n; j++) {
                sumFirstHalf += b.get(j);
            }
            for (int j = n; j < 2 * n; j++) {
                sumSecondHalf += b.get(j);
            }
            maxScore = Math.max(maxScore, sumFirstHalf - sumSecondHalf);
        }
        System.out.println(maxScore);
    }
}
