#include <stdio.h>

#define MOD 1000000007

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    int l[m], r[m], x[m];
    for (int i = 0; i < m; i++) {
        scanf("%d %d %d", &l[i], &r[i], &x[i]);
        l[i]--; 
        r[i]--; 
    }

    long long count = 0;
    for (int i = 0; i < pow(3, n); i++) {
        int colors[n];
        int temp = i;
        for (int j = n - 1; j >= 0; j--) {
            colors[j] = temp % 3;
            temp /= 3;
        }

        int satisfied = 1;
        for (int j = 0; j < m; j++) {
            int distinct_colors = 0;
            int seen[3] = {0};
            for (int k = l[j]; k <= r[j]; k++) {
                if (seen[colors[k]] == 0) {
                    distinct_colors++;
                    seen[colors[k]] = 1;
                }
            }
            if (distinct_colors != x[j]) {
                satisfied = 0;
                break;
            }
        }
        if (satisfied) {
            count++;
        }
    }

    printf("%lld\n", count);
    return 0;
}
