#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, m;
  cin >> n >> m;

  vector<tuple<int, int, int>> conditions(m);
  for (int i = 0; i < m; ++i) {
    cin >> get<0>(conditions[i]) >> get<1>(conditions[i]) >> get<2>(conditions[i]);
    get<0>(conditions[i])--; 
    get<1>(conditions[i])--; 
  }

  long long count = 0;
  long long mod = 1e9 + 7;

  vector<int> colors(n);
  function<void(int)> solve = [&](int k) {
    if (k == n) {
      bool satisfied = true;
      for (int i = 0; i < m; ++i) {
        vector<bool> seen(4, false);
        int distinct_colors = 0;
        for (int j = get<0>(conditions[i]); j <= get<1>(conditions[i]); ++j) {
          if (!seen[colors[j]]) {
            seen[colors[j]] = true;
            distinct_colors++;
          }
        }
        if (distinct_colors != get<2>(conditions[i])) {
          satisfied = false;
          break;
        }
      }
      if (satisfied) {
        count = (count + 1) % mod;
      }
      return;
    }

    for (int i = 1; i <= 3; ++i) {
      colors[k] = i;
      solve(k + 1);
    }
  };

  solve(0);
  cout << count << endl;
  return 0;
}
