import java.util.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int[][] conditions = new int[m][3];
        for (int i = 0; i < m; i++) {
            conditions[i][0] = sc.nextInt();
            conditions[i][1] = sc.nextInt();
            conditions[i][2] = sc.nextInt();
        }
        long count = 0;
        for (int i = 0; i < Math.pow(3, n); i++) {
            int[] colors = new int[n];
            int temp = i;
            for (int j = n - 1; j >= 0; j--) {
                colors[j] = temp % 3;
                temp /= 3;
            }
            boolean satisfied = true;
            for (int j = 0; j < m; j++) {
                int l = conditions[j][0] - 1;
                int r = conditions[j][1] - 1;
                int x = conditions[j][2];
                Set<Integer> distinctColors = new HashSet<>();
                for (int k = l; k <= r; k++) {
                    distinctColors.add(colors[k]);
                }
                if (distinctColors.size() != x) {
                    satisfied = false;
                    break;
                }
            }
            if (satisfied) {
                count++;
            }
        }
        System.out.println(count % MOD);
    }
}
