#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_SIZE 100

int h, w;
char pond[MAX_SIZE][MAX_SIZE];
int visited[MAX_SIZE][MAX_SIZE];
int sx, sy, tx, ty;

int dfs(int x, int y) {
    visited[x][y] = 1;
    if (x == tx && y == ty) return 1;
    for (int i = 0; i < h; i++) {
        if (pond[i][y] != '.' && !visited[i][y]) {
            if (dfs(i, y)) return 1;
        }
    }
    for (int j = 0; j < w; j++) {
        if (pond[x][j] != '.' && !visited[x][j]) {
            if (dfs(x, j)) return 1;
        }
    }
    return 0;
}

int main() {
    scanf("%d %d", &h, &w);
    for (int i = 0; i < h; i++) {
        char row[MAX_SIZE + 1];
        scanf("%s", row);
        for (int j = 0; j < w; j++) {
            pond[i][j] = row[j];
            if (pond[i][j] == 'S') {
                sx = i;
                sy = j;
            } else if (pond[i][j] == 'T') {
                tx = i;
                ty = j;
            }
        }
    }
    int count = 0;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (pond[i][j] == 'o') {
                count++;
            }
        }
    }

    int min_remove = -1;
    for (int i = 0; i < (1 << count); i++) {
        int removed = 0;
        int k = i;
        char temp_pond[MAX_SIZE][MAX_SIZE];
        memcpy(temp_pond, pond, sizeof(pond));
        for (int r = 0; r < h; r++) {
            for (int c = 0; c < w; c++) {
                if (temp_pond[r][c] == 'o') {
                    if (k % 2 == 1) {
                        temp_pond[r][c] = '.';
                        removed++;
                    }
                    k /= 2;
                }
            }
        }
        memset(visited, 0, sizeof(visited));
        if (!dfs(sx, sy)) {
            if (min_remove == -1 || removed < min_remove) {
                min_remove = removed;
            }
        }
    }
    printf("%d\n", min_remove);
    return 0;
}
