#include <stdio.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int count[N + 1];
    for (int i = 1; i <= N; i++) {
        count[i] = 0;
    }
    for (int i = 0; i < M; i++) {
        int a, b;
        scanf("%d %d", &a, &b);
        count[a]++;
        count[b]++;
    }
    for (int i = 1; i <= N; i++) {
        printf("%d\n", count[i]);
    }
    return 0;
}
