#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, m;
  cin >> n >> m;

  vector<int> city_count(n + 1, 0);
  for (int i = 0; i < m; ++i) {
    int u, v;
    cin >> u >> v;
    city_count[u]++;
    city_count[v]++;
  }

  for (int i = 1; i <= n; ++i) {
    cout << city_count[i] << endl;
  }

  return 0;
}
