#include <stdio.h>
#include <stdlib.h>

int compare(const void *a, const void *b) {
    return *(int *)a - *(int *)b;
}

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    int *arr = (int *)malloc(sizeof(int) * (N * 100000));
    int count = 0;
    for (int i = 0; i < N; i++) {
        int a, b;
        scanf("%d %d", &a, &b);
        for (int j = 0; j < b; j++) {
            arr[count++] = a;
        }
    }
    qsort(arr, count, sizeof(int), compare);
    printf("%d\n", arr[K - 1]);
    free(arr);
    return 0;
}
