#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    long long count = 0;
    long long dp[n + 1][2];
    dp[0][0] = 1;
    dp[0][1] = 0;
    for (int i = 0; i < n; i++) {
        dp[i + 1][0] = 0;
        dp[i + 1][1] = 0;
        for (int j = a[i] - 1; j <= a[i] + 1; j++) {
            if (j > 0) {
                dp[i + 1][j % 2] += dp[i][0];
                dp[i + 1][1] += dp[i][0];

                dp[i + 1][(j % 2)] += dp[i][1];
            }

        }
    }
    printf("%lld\n", dp[n][1]);
    return 0;
}
