#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n, k;
  cin >> n >> k;

  vector<pair<long long, long long>> ops(n);
  for (int i = 0; i < n; ++i) {
    cin >> ops[i].first >> ops[i].second;
  }

  vector<long long> arr;
  for (int i = 0; i < n; ++i) {
    for (long long j = 0; j < ops[i].second; ++j) {
      arr.push_back(ops[i].first);
    }
  }

  sort(arr.begin(), arr.end());

  cout << arr[k - 1] << endl;
  return 0;
}
