import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long k = sc.nextInt();
        List<Pair> list = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            list.add(new Pair(sc.nextInt(), sc.nextInt()));
        }
        Collections.sort(list, Comparator.comparingInt(p -> p.a));
        long count = 0;
        for (Pair p : list) {
            count += p.b;
            if (count >= k) {
                System.out.println(p.a);
                break;
            }
        }
        sc.close();
    }

    static class Pair {
        int a;
        long b;

        Pair(int a, long b) {
            this.a = a;
            this.b = b;
        }
    }
}
