#include <stdio.h>

int main() {
    long long a, b, c;
    scanf("%lld %lld %lld", &a, &b, &c);

    int count = 0;
    while (a % 2 == 0 && b % 2 == 0 && c % 2 == 0) {
        long long next_a = (a + b + c) / 2;
        long long next_b = (a + b + c) / 2;
        long long next_c = (a + b + c) / 2;
        a = next_a;
        b = next_b;
        c = next_c;
        count++;
        if (count > 1000) {
            printf("-1\n");
            return 0;
        }
    }
    printf("%d\n", count);
    return 0;
}
