#include <iostream>

using namespace std;

int main() {
  long long a, b, c;
  cin >> a >> b >> c;

  long long count = 0;
  while (a % 2 == 0 && b % 2 == 0 && c % 2 == 0) {
    long long next_a = (a / 2) + (b / 2) + (c / 2);
    long long next_b = (a / 2) + (b / 2) + (c / 2);
    long long next_c = (a / 2) + (b / 2) + (c / 2);
    a = next_a;
    b = next_b;
    c = next_c;
    count++;
    if (count > 1000) {
      cout << -1 << endl;
      return 0;
    }
  }
  cout << count << endl;
  return 0;
}
