#include <stdio.h>
#include <stdlib.h>

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    int degree[n + 1];
    for (int i = 1; i <= n; i++) {
        degree[i] = 0;
    }
    int a, b;
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &a, &b);
        degree[a]++;
        degree[b]++;
    }
    int odd_degree = 0;
    for (int i = 1; i <= n; i++) {
        if (degree[i] % 2 != 0) {
            odd_degree++;
        }
    }
    if (odd_degree == 0 || odd_degree == 2) {
        printf("YES\n");
    } else {
        printf("NO\n");
    }
    return 0;
}
