#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long count = 0;
  vector<long long> b(n);
  vector<long long> range(n);

  function<void(int)> generate = [&](int k) {
    if (k == n) {
      long long product = 1;
      for (long long x : b) {
        product *= x;
      }
      if (product % 2 == 0) {
        count++;
      }
      return;
    }

    for (long long i = a[k] - 1; i <= a[k] + 1; ++i) {
      if (i > 0) {
        b[k] = i;
        generate(k + 1);
      }
    }
  };

  generate(0);
  cout << count << endl;
  return 0;
}
