#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_H 800
#define MAX_W 800

int H, W, K;
char A[MAX_H][MAX_W];
int dist[MAX_H][MAX_W];
int dx[] = {0, 0, 1, -1};
int dy[] = {1, -1, 0, 0};

int bfs(int sx, int sy) {
    memset(dist, -1, sizeof(dist));
    int q[MAX_H * MAX_W][2], head = 0, tail = 0;
    q[tail][0] = sx;
    q[tail++][1] = sy;
    dist[sx][sy] = 0;
    while (head < tail) {
        int x = q[head][0];
        int y = q[head++][1];
        if (x == 0 || x == H - 1 || y == 0 || y == W - 1) return dist[x][y];
        for (int i = 0; i < 4; i++) {
            int nx = x + dx[i];
            int ny = y + dy[i];
            if (nx >= 0 && nx < H && ny >= 0 && ny < W && A[nx][ny] != '#' && dist[nx][ny] == -1) {
                dist[nx][ny] = dist[x][y];
                q[tail][0] = nx;
                q[tail++][1] = ny;
            }
        }
    }
    return -1;
}

int main() {
    scanf("%d %d %d", &H, &W, &K);
    int sx, sy;
    for (int i = 0; i < H; i++) {
        char row[MAX_W + 1];
        scanf("%s", row);
        for (int j = 0; j < W; j++) {
            A[i][j] = row[j];
            if (row[j] == 'S') {
                sx = i;
                sy = j;
            }
        }
    }
    int ans = 0;
    while (1) {
        int d = bfs(sx, sy);
        if (d != -1) {
            ans++;
            break;
        }
        ans++;
    }
    printf("%d\n", ans);
    return 0;
}
