#include <iostream>
#include <vector>
#include <queue>

using namespace std;

int main() {
    int h, w, k;
    cin >> h >> w >> k;

    vector<string> a(h);
    for (int i = 0; i < h; ++i) {
        cin >> a[i];
    }

    int start_row, start_col;
    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            if (a[i][j] == 'S') {
                start_row = i;
                start_col = j;
            }
        }
    }

    queue<pair<pair<int, int>, pair<vector<string>, int>>> q;
    q.push({{start_row, start_col}, {a, 0}});

    vector<vector<vector<string>>> visited;

    while (!q.empty()) {
        pair<pair<int, int>, pair<vector<string>, int>> current = q.front();
        q.pop();

        int row = current.first.first;
        int col = current.first.second;
        vector<string> current_a = current.second.first;
        int casts = current.second.second;

        if (row == 0 || row == h - 1 || col == 0 || col == w - 1) {
            cout << casts << endl;
            return 0;
        }


        bool visited_state = false;
        for(auto v : visited){
            if(v == current_a){
                visited_state = true;
                break;
            }
        }
        if(visited_state) continue;
        visited.push_back(current_a);

        for (int i = 0; i <=k; ++i){
            vector<string> next_a = current_a;

            queue<pair<int,int>> move_q;
            move_q.push({row,col});
            vector<vector<bool>> visited_move(h, vector<bool>(w, false));
            visited_move[row][col] = true;
            int moves = 0;
            vector<pair<int,int>> unlocked;

            while(!move_q.empty() && moves < k){
                int q_size = move_q.size();
                for(int j=0; j< q_size; ++j){
                    pair<int,int> curr = move_q.front();
                    move_q.pop();
                    int r = curr.first;
                    int c = curr.second;
                    if(r == 0 || r == h-1 || c == 0 || c == w-1){
                        cout << casts + 1 << endl;
                        return 0;
                    }
                    int dr[] = {-1,1,0,0};
                    int dc[] = {0,0,-1,1};
                    for(int l=0; l<4; ++l){
                        int nr = r + dr[l];
                        int nc = c + dc[l];
                        if(nr >=0 && nr < h && nc >= 0 && nc < w && !visited_move[nr][nc] && next_a[nr][nc] != '#'){
                            visited_move[nr][nc] = true;
                            move_q.push({nr,nc});
                        }
                    }
                }
                moves++;
            }


            int unlocked_count = 0;
            for(int r=0; r<h; ++r){
                for(int c=0; c<w; ++c){
                    if(next_a[r][c] == '#' && unlocked_count < k){
                        next_a[r][c] = '.';
                        unlocked_count++;
                    }
                }
            }
            q.push({{row, col}, {next_a, casts + 1}});
        }
    }

    return 0;
}
