import java.util.*;
import java.io.*;

public class Main {
    static final int INF = 1000000000;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int H = Integer.parseInt(st.nextToken());
        int W = Integer.parseInt(st.nextToken());
        int K = Integer.parseInt(st.nextToken());
        char[][] A = new char[H][W];
        for (int i = 0; i < H; i++) {
            String s = br.readLine();
            for (int j = 0; j < W; j++) {
                A[i][j] = s.charAt(j);
            }
        }

        int startRow = -1, startCol = -1;
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (A[i][j] == 'S') {
                    startRow = i;
                    startCol = j;
                    break;
                }
            }
        }

        Queue<Pair> q = new LinkedList<>();
        q.offer(new Pair(startRow, startCol, 0, 0));
        int[][] dist = new int[H][W];
        for (int i = 0; i < H; i++) {
            Arrays.fill(dist[i], INF);
        }
        dist[startRow][startCol] = 0;

        int[] dr = {-1, 1, 0, 0};
        int[] dc = {0, 0, -1, 1};

        while (!q.isEmpty()) {
            Pair p = q.poll();
            int r = p.r;
            int c = p.c;
            int casts = p.casts;
            int unlocked = p.unlocked;

            if (r == 0 || r == H - 1 || c == 0 || c == W - 1) {
                System.out.println(casts);
                return;
            }

            for (int i = 0; i < 4; i++) {
                int nr = r + dr[i];
                int nc = c + dc[i];

                if(nr>=0 && nr<H && nc>=0 && nc<W && A[nr][nc] != '#' && dist[nr][nc] > casts){
                    dist[nr][nc] = casts;
                    q.offer(new Pair(nr, nc, casts, unlocked));
                }
            }
            if(unlocked < K){
                for(int i=0; i<H; i++){
                    for(int j=0; j<W; j++){
                        if(A[i][j] == '#' && dist[i][j] > casts + 1){
                            dist[i][j] = casts + 1;
                            q.offer(new Pair(i,j, casts + 1, unlocked + 1));
                        }
                    }
                }
            }
        }
    }

    static class Pair {
        int r, c, casts, unlocked;

        Pair(int r, int c, int casts, int unlocked) {
            this.r = r;
            this.c = c;
            this.casts = casts;
            this.unlocked = unlocked;
        }
    }
}
