#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<tuple<int, int>> edges(n - 1);
  for (int i = 0; i < n - 1; ++i) {
    cin >> get<0>(edges[i]) >> get<1>(edges[i]);
    get<0>(edges[i])--;
    get<1>(edges[i])--;
  }

  if (n % 2 != 0) {
    cout << "First" << endl;
  } else {
    cout << "Second" << endl;
  }
  return 0;
}
