import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            graph.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int a = sc.nextInt() - 1;
            int b = sc.nextInt() - 1;
            graph.get(a).add(b);
            graph.get(b).add(a);
        }
        if (n % 2 == 1) {
            System.out.println("First");
        } else {
            boolean leaf = true;
            for (int i = 0; i < n; i++) {
                if (graph.get(i).size() > 1) {
                    leaf = false;
                    break;
                }
            }
            if (leaf) System.out.println("Second");
            else System.out.println("First");

        }
    }
}
