import collections

def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u - 1, v - 1))

    adj = collections.defaultdict(list)
    for u, v in edges:
        adj[u].append(v)
        adj[v].append(u)

    if n % 2 != 0:
        print("First")
    else:
        leaves = 0
        for i in range(n):
            if len(adj[i]) == 1:
                leaves +=1

        if leaves <= n //2 :
            print("Second")
        else:
            print("First")

solve()
