import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        long count = 0;
        long total = 1;
        for (int i = 0; i < n; i++) {
            total *= 3;
        }
        long odd = 0;
        for (int i = 0; i < total; i++) {
            int temp = i;
            int[] b = new int[n];
            boolean possible = true;
            for (int j = 0; j < n; j++) {
                int digit = temp % 3;
                temp /= 3;
                b[j] = a[j] - 1 + digit;
                if (Math.abs(a[j] - b[j]) > 1) {
                    possible = false;
                    break;
                }
            }
            if (possible) {
                boolean even = false;
                for (int j = 0; j < n; j++) {
                    if (b[j] % 2 == 0) {
                        even = true;
                        break;
                    }
                }
                if (even) count++;
                else odd++;
            }
        }
        System.out.println(count);
    }
}
