#include <stdio.h>
#include <stdlib.h>

int main() {
    int N;
    scanf("%d", &N);
    if (N == 2) {
        int a, b, c, d;
        scanf("%d %d %d %d", &a, &b, &c, &d);
        if ((a == c && b == d) || (a == d && b == c))
            printf("YES\n");
        else
            printf("NO\n");

        return 0;
    }

    int blue_edges[N - 1][2];
    int red_edges[N - 1][2];
    for (int i = 0; i < N - 1; i++) {
        scanf("%d %d", &blue_edges[i][0], &blue_edges[i][1]);
    }
    for (int i = 0; i < N - 1; i++) {
        scanf("%d %d", &red_edges[i][0], &red_edges[i][1]);
    }

    int degree[N + 1];
    for (int i = 1; i <= N; i++) degree[i] = 0;
    for (int i = 0; i < N - 1; i++) {
        degree[blue_edges[i][0]]++;
        degree[blue_edges[i][1]]++;
    }

    int red_degree[N + 1];
    for (int i = 1; i <= N; i++) red_degree[i] = 0;
    for (int i = 0; i < N - 1; i++) {
        red_degree[red_edges[i][0]]++;
        red_degree[red_edges[i][1]]++;
    }


    int possible = 1;
    for (int i = 1; i <=N; i++){
        if (degree[i] != red_degree[i]) possible = 0;
    }

    if (possible) printf("YES\n");
    else printf("NO\n");

    return 0;
}
