      if (get<0>(red_edges[0]) == get<0>(red_edges[1]) || get<0>(red_edges[0]) == get<1>(red_edges[1]) || get<1>(red_edges[0]) == get<0>(red_edges[1]) || get<1>(red_edges[0]) == get<1>(red_edges[1])) possible = false;
      if (possible) cout << "YES" << endl;
      else cout << "NO" << endl;
      return 0;
  }


  cout << "YES" << endl; 

