#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    int p[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &p[i]);
    }
    int count = 0;
    while (1) {
        int sorted = 1;
        for (int i = 0; i < N; i++) {
            if (p[i] != i + 1) {
                sorted = 0;
                break;
            }
        }
        if (sorted) break;
        int max = 0;
        int high[N];
        int low[N];
        int high_count = 0;
        int low_count = 0;
        for (int i = 0; i < N; i++) {
            if (p[i] > max) {
                max = p[i];
                high[high_count++] = p[i];
            } else {
                low[low_count++] = p[i];
            }
        }
        int k = 0;
        for (int i = 0; i < low_count; i++) {
            p[k++] = low[i];
        }
        for (int i = 0; i < high_count; i++) {
            p[k++] = high[i];
        }
        count++;
    }
    printf("%d\n", count);
    return 0;
}
