#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<int> p(n);
    for (int i = 0; i < n; ++i) {
        cin >> p[i];
    }

    int count = 0;
    while (true) {
        bool sorted = true;
        for (int i = 0; i < n; ++i) {
            if (p[i] != i + 1) {
                sorted = false;
                break;
            }
        }
        if (sorted) {
            break;
        }

        vector<int> high;
        vector<int> low;
        int max_so_far = 0;
        for (int i = 0; i < n; ++i) {
            if (p[i] > max_so_far) {
                max_so_far = p[i];
                high.push_back(p[i]);
            } else {
                low.push_back(p[i]);
            }
        }
        p.clear();
        p.insert(p.end(), low.begin(), low.end());
        p.insert(p.end(), high.begin(), high.end());
        count++;
    }
    cout << count << endl;
    return 0;
}
