import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] p = new int[n];
        for (int i = 0; i < n; i++) {
            p[i] = sc.nextInt();
        }
        int count = 0;
        while (!isSorted(p)) {
            List<Integer> low = new ArrayList<>();
            List<Integer> high = new ArrayList<>();
            int max = 0;
            for (int i = 0; i < n; i++) {
                max = Math.max(max, p[i]);
                if (max == p[i]) {
                    high.add(p[i]);
                } else {
                    low.add(p[i]);
                }
            }
            int[] next = new int[n];
            int k = 0;
            for (int i = 0; i < low.size(); i++) {
                next[k++] = low.get(i);
            }
            for (int i = 0; i < high.size(); i++) {
                next[k++] = high.get(i);
            }
            p = next;
            count++;
        }
        System.out.println(count);
    }
    public static boolean isSorted(int[] p) {
        for (int i = 0; i < p.length - 1; i++) {
            if (p[i] > p[i + 1]) {
                return false;
            }
        }
        return true;
    }
}
